(()=>{async function e(){try{const e=await chrome.runtime.sendMessage({action:"genKeys"});if(console.log("Response from background:",e),e.success){const n=`\n        <strong>Key Pair Generated!</strong><br><br>\n        Public Key Length: ${e.publicKeyLength} bytes<br>\n        <small>Public Key (base64): ${e.publicKey.substring(0,50)}...</small><br>\n\n        Secret Key Length: ${e.secretKeyLength} bytes<br><br>\n        <small>Secret Key (base64): ${e.secretKey.substring(0,50)}...</small>\n      `;document.getElementById("keyGenOutput").innerHTML=n}else document.getElementById("keyGenOutput").innerHTML=`<strong>Error:</strong> ${e.error}`}catch(e){console.error("Error:",e),document.getElementById("keyGenOutput").innerHTML=`<strong>Error:</strong> ${e.message}`}}async function n(){try{const e=await chrome.runtime.sendMessage({action:"randomNumber"});if(console.log("Response from background:",e),e.success){const n=`<p>Random Number: ${e.ranNum}</p>`;document.getElementById("ranNumOut").innerHTML=n}else document.getElementById("ranNumOut").innerHTML=`<strong>Error:</strong> ${e.error}`}catch(e){console.error("Error:",e),document.getElementById("ranNumOut").innerHTML=`<strong>Error:</strong> ${e.message}`}}console.log("\n--- popup.js loading ---\n"),document.addEventListener("DOMContentLoaded",()=>{const t=document.getElementById("genKeysBtn");t?(t.addEventListener("click",e),console.log("Key generation button listener attached")):console.error("genKeysBtn not found in DOM");const r=document.getElementById("ranNumBtn");r?(r.addEventListener("click",n),console.log("random number button listener")):console.error("ranNumBttn not found")}),console.log("\n--- popup.js completed ---\n")})();